import React, { useState } from 'react';

const Projects = ({ projects }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredProjects = projects.filter(project =>
    project.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    project.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
    project.tech_stack.some(skill => skill.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="p-6 bg-gray-900 rounded-xl shadow-2xl">
      <h2 className="text-4xl font-extrabold text-white mb-6">Projects</h2>
      <input
        type="text"
        placeholder="Search projects by title, description, or skill..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="w-full p-3 mb-8 rounded-lg bg-gray-800 text-gray-200 placeholder-gray-500 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
      />
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredProjects.length > 0 ? (
          filteredProjects.map(project => (
            <div key={project._id} className="bg-gray-800 p-6 rounded-lg shadow-md hover:bg-gray-700 transition-all duration-300 transform hover:scale-105">
              <h3 className="text-2xl font-bold text-white mb-2">{project.title}</h3>
              <p className="text-gray-400 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.tech_stack.map((skill, index) => (
                  <span key={index} className="bg-blue-600 text-white text-xs font-semibold px-2.5 py-1 rounded-full">
                    {skill}
                  </span>
                ))}
              </div>
              <div className="flex space-x-4">
                {project.links.github && (
                  <a href={project.links.github} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:text-blue-300 transition-colors">
                    GitHub
                  </a>
                )}
                {project.links.live && (
                  <a href={project.links.live} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:text-blue-300 transition-colors">
                    Live Demo
                  </a>
                )}
              </div>
            </div>
          ))
        ) : (
          <p className="col-span-full text-center text-gray-500 italic">No projects found matching your search.</p>
        )}
      </div>
    </div>
  );
};

export default Projects;
